import { Logger } from "@azure/msal-common";
import { TokenValidator } from "../../src/crypto/TokenValidator";
import { TEST_CONSTANTS, TEST_APP_SETTINGS, TEST_MSAL_CONFIG } from "../TestConstants";
import { DEFAULT_LOGGER_OPTIONS } from "../../src/utils/Constants"

describe('Token validator tests', () => {

    const appSettings = TEST_APP_SETTINGS;
    const msalConfig = TEST_MSAL_CONFIG;
    const logger = new Logger(DEFAULT_LOGGER_OPTIONS);

    const tokenValidator = new TokenValidator(appSettings, msalConfig, logger);
    const testTokenPayload = "eyJ0eXAiOiJKV1QiLCJhbGciOiJSUzI1NiIsImtpZCI6IjFMVE16YWtpaGlSbGFfOHoyQkVKVlhlV01xbyJ9..1AFWW-Ck5nROwSlltm7GzZvDwUkqvhSQpm55TQsmVo9Y59cLhRXpvB8n-55HCr9Z6G_31_UbeUkoz612I2j_Sm9FFShSDDjoaLQr54CreGIJvjtmS3EkK9a7SJBbcpL1MpUtlfygow39tFjY7EVNW9plWUvRrTgVk7lYLprvfzw-CIqw3gHC-T7IK_m_xkr08INERBtaecwhTeN4chPC4W3jdmw_lIxzC48YoQ0dB1L9-ImX98Egypfrlbm0IBL5spFzL6JDZIRRJOu8vecJvj1mq-IUhGt0MacxX8jdxYLP-KUu2d9MbNKpCKJuZ7p8gwTL5B7NlUdh_dmSviPWrw";
    
    it('should generate a valid instance', () => {
        expect(tokenValidator).toBeInstanceOf(TokenValidator);
    });

    it('should decode a token', () => {
        const decodedToken = TokenValidator.decodeAuthToken(testTokenPayload);
        expect(decodedToken.header).toMatchObject(TEST_CONSTANTS.ID_TOKEN_HEADER);
        expect(decodedToken.payload).toMatchObject(TEST_CONSTANTS.ID_TOKEN_CLAIMS);
    });
})